CREATE TABLE IF NOT EXISTS  `customer_bank_detail` ( `id` INT(11) NOT NULL AUTO_INCREMENT , `customer_id` INT(11) DEFAULT NULL, `acc_holder_name` VARCHAR(100) DEFAULT NULL , `bank_branch` VARCHAR(255) DEFAULT NULL , `bank_address` VARCHAR(255) DEFAULT NULL , `bank_code` INT DEFAULT NULL , `branch_number` INT DEFAULT NULL , `acc_number` INT(20) DEFAULT NULL , `suffix` INT DEFAULT NULL , `is_active` TINYINT(1) DEFAULT NULL , `status` TINYINT(4) DEFAULT NULL , `created_by` INT DEFAULT NULL , `updated_by` INT DEFAULT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`));