CREATE TABLE  IF NOT EXISTS `delivered_product_detail` ( `id` INT(11) NOT NULL AUTO_INCREMENT , `order_id` INT(11) DEFAULT NULL , `customer_id` INT(11) DEFAULT NULL , `product_id` INT(11) DEFAULT NULL , `related_to` VARCHAR(50) DEFAULT NULL , `invoice_number` VARCHAR(25) DEFAULT NULL , `purchase_from` VARCHAR(255) DEFAULT NULL , `product_cost` DOUBLE(10,2) DEFAULT NULL , `product_color` VARCHAR(50) DEFAULT NULL , `color_id` INT DEFAULT NULL , `product_brand` VARCHAR(50) DEFAULT NULL , `brand_id` INT DEFAULT NULL , `delivery_date` DATETIME DEFAULT NULL , `specification` VARCHAR(1000) DEFAULT NULL , `status` INT DEFAULT NULL , `is_active` TINYINT(1) DEFAULT NULL , `created_by` INT DEFAULT NULL , `updated_by` INT DEFAULT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`));